export const authActionsTypes = {
  LOG_IN: "LOG_IN",
  LOG_OUT: "LOG_OUT",
};

export const initialSTATE = {
  email: "",
  isAUTOHRIZED: false,
};

(id) => async (dispatch) => {
  try {
    await  fetch  (`basketItem/${id}/delete`, {
      method: "DELETE",
    });
    dispatch(getBasket());
  } catch (error) {
    console.log(error);
  }
};






export const authReducer = (state = initialSTATE, action) => {
  switch (action.type) {
    case authActionsTypes.LOG_IN:
      return {
        ...state,
        email: action.payload,
        isAUTOHRIZED: true,
      };

    case authActionsTypes.LOG_OUT:
      return initialSTATE;

    default:
      return state;
  }
};
