import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { authActionsTypes } from '../store/auth/authReducer';
import classes from './Auth.module.css';

const Auyh =()=>{
  const dispatch=useDispatch()
}

const [formState,setState]=useState({
  email:"",
  password:""
})

const inputChangeHandler=(name)=>{
  return(event)=>{

    setState(prevState=>({...prevState, [name]:event.target.value}))
  }
}
const submitHandler=()=>{
  if(formState.email === "test@gmail.com" && formState.password==="123123"){
    dispatch({
      type:authActionsTypes.LOG_IN,
      payload:formState.email
    })
  }
}
const Auth = () => {
  return (
    <main className={classes.auth}>
      <section>
        <form onSubmit={submitHandler}>
          <div className={classes.control}>
            <label htmlFor="email" onChange={inputChangeHandler("email")}>
              Email
            </label>
            <input type="email" id="email" />
          </div>
          <div className={classes.control}>
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              onChange={inputChangeHandler("email")}
            />
          </div>
          <button>Login</button>
        </form>
      </section>
    </main>
  );
};

export default Auth;
